export const NOTIFICATION_SOUND = 'data:audio/mp3;base64,';
// Note: This is a placeholder. For a real "pop" sound, we should use a real file. 
// However, to ensure it works immediately without file dependencies, I will use a simple beep function or the user should provide a file.
// Let's actually use a simple Web Audio API beep for reliability if the file is missing.

export function playNotificationSound() {
    try {
        const audio = new Audio('/sounds/notification.mp3');
        audio.play().catch(e => {
            console.warn('Audio file not found, playing fallback beep');
            playFallbackBeep();
        });
    } catch (e) {
        playFallbackBeep();
    }
}

function playFallbackBeep() {
    const ctx = new (window.AudioContext || (window as any).webkitAudioContext)();
    const osc = ctx.createOscillator();
    const gain = ctx.createGain();

    osc.connect(gain);
    gain.connect(ctx.destination);

    osc.type = 'sine';
    osc.frequency.setValueAtTime(500, ctx.currentTime);
    osc.frequency.exponentialRampToValueAtTime(1000, ctx.currentTime + 0.1);

    gain.gain.setValueAtTime(0.1, ctx.currentTime);
    gain.gain.exponentialRampToValueAtTime(0.01, ctx.currentTime + 0.1);

    osc.start();
    osc.stop(ctx.currentTime + 0.1);
}
